import { useEffect } from "react";
import { useParams } from "react-router"
import axios from "axios";

const SingleBuilding = () => {
  const { buildingId } = useParams()

  const [property, setProperty] = useState();

  useEffect(async () => {
    const resApi = await axios.get(`https://api.simplyrets.com/properties/${buildingId}`, {
      auth: {
        username: 'simplyrets',
        password: 'simplyrets',
      },
    });

    console.log("building data single===>>>", resApi.data);
    setProperty(resApi.data);
  }, []);


  return (
    <>
      <div className="navbar">
        <img src="images/logo.png" alt="Logo" className="navbarLogo" />
        <h2>ApnaGhar</h2>
      </div>
      <div className="propertyMain">
        <div className="propertyWrapper">
          <div className="propertyTopbar">
            <h4>Property Listings</h4>
          </div>
          <div className="propertyCard">
            <div className="propertyComp" onClick={pageNavigation}>
              <img
                className="heart"
                src={heart ? 'images/heart-fill.svg' : 'images/heart-stroke.svg'}
                onClick={heartStorageHandler}
              />
              <img className="propertyImg" src={detail?.photos[0]} alt="" />
              <div className="propertyText">
                <h5>
                  {detail?.property?.bedrooms} BR | {detail?.property?.bathsFull}
                  {detail?.property?.bathsHalf} Bath | {detail?.property?.area} Sq Ft
                </h5>
                <h3>${detail?.listPrice}</h3>
                <h6>
                  {detail?.address?.streetNumber} {detail?.address?.streetName},{' '}
                  {detail?.address?.city}
                </h6>
                <span className="listed">
                  Listed: {detail?.listDate?.split('T')[0].split('-')[2]}/
                  {detail?.listDate?.split('T')[0].split('-')[1]}/
                  {detail?.listDate?.split('T')[0].split('-')[0].slice(2)}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default SingleBuilding
